void Screen3ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &button_back)
    {
        //backInteraction
        //When button_back clicked change screen to Screen1
        //Go to Screen1 with screen transition towards East
        application().gotoScreen1ScreenWipeTransitionEast();

        //saveInteraction
        //When backInteraction completed call virtual function
        //Call button_backButtonPressed_save
        button_backButtonPressed_save();
    }
    else if (&src == &button_zoom)
    {
        //zoomButtonInteraction
        //When button_zoom clicked call virtual function
        //Call zoomButtonPressed
        zoomButtonPressed();
    }
    else if (&src == &button_confirm)
    {
        //button_confirmInteraction
        //When button_confirm clicked call virtual function
        //Call button_confirmButtonPressed
        button_confirmButtonPressed();
    }
    else if (&src == &button_cancel)
    {
        //button_cancelInteraction
        //When button_cancel clicked call virtual function
        //Call button_cancelButtonPressed
        button_cancelButtonPressed();
    }
}

